import React, { useState, useEffect } from 'react'
// import { Link } from 'react-router-dom'

const Admin = () => {
  const [items, setItems] = useState([])
  const [sales, setSales] = useState([])

  useEffect(() => {
    fetch('/admin/items')
      .then((res) => res.json())
      .then((data) => {
        setItems(data)
      })
      .catch((err) => console.log(err))
  }, [])

  useEffect(() => {
    fetch('/admin/sales')
      .then((res) => res.json())
      .then((data) => {
        setSales(data)
      })
      .catch((err) => console.log(err))
  }, [])

  const addItem = (item) => {
    fetch('/admin/items', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ item })
    })
      .then((res) => res.json())
      .then((data) => {
        setItems(data)
      })
      .catch((err) => console.log(err))
  }

  return (
    <div>
      <h1>Admin Dashboard</h1>
      <h2>Items</h2>
      <ul>
        {items.map((item) => (
          <li key={item.id}>{item.name} - {item.price}</li>
        ))}
      </ul>
      <button onClick={() => addItem({ name: 'New Item', price: 10 })}>Add Item</button>
      <h2>Sales</h2>
      <ul>
        {sales.map((sale) => (
          <li key={sale.id}>{sale.date} - {sale.total}</li>
        ))}
      </ul>
    </div>
  )
}

export default Admin
